import axios from 'axios'

const api = {
    async generateTTS(data) {
        return await axios.post('/api/tts/generate', data, {
            headers: {
                'Content-Type': 'application/json'
            }
        });
    },

    async getSpeakers() {
        return await axios.get('/api/tts/speakers');
    }
};

export default {
    // TTS 相关
    generateTTS(data) {
        return api.post('/api/tts/generate', data)
    },

    getSpeakers() {
        return api.get('/api/tts/speakers')
    },

    getModels() {
        return api.get('/api/tts/models')
    },

    // 语音克隆相关
    cloneVoice(data) {
        return api.post('/api/voice-clone/generate', data)
    },

    uploadSample(file) {
        const formData = new FormData()
        formData.append('file', file)

        return api.post('/api/voice-clone/upload-sample', formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
    }
}
