// store/modules/tts.js
import api from '@/services/api'

export default {
    namespaced: true,
    state: {
        speakers: [],
        models: [],
        isLoading: false,
        error: null
    },
    mutations: {
        SET_SPEAKERS(state, speakers) {
            // 关键：如果传入的 speakers 是 null/undefined，默认设为空数组
            state.speakers = Array.isArray(speakers) ? speakers : [];
        },
        SET_LOADING(state, isLoading) {
            state.isLoading = isLoading;
        },
        SET_ERROR(state, error) {
            state.error = error;
        }
    },
    actions: {
        async fetchSpeakers({ commit }) {
            try {
                commit('SET_LOADING', true)
                const response = await api.getSpeakers()
                if (response.data.success) {
                    commit('SET_SPEAKERS', response.data.speakers)
                } else {
                    throw new Error(response.data.message || '获取说话人失败')
                }
            } catch (error) {
                commit('SET_ERROR', error.message)
                console.error('获取说话人失败:', error)
            } finally {
                commit('SET_LOADING', false)
            }
        }
    },
    getters: {
        speakers: state => state.speakers,
        isLoading: state => state.isLoading,
        error: state => state.error
    }
}
